            tooltip_str = '<center><b>'+x+'</b></center>' + y;
            return tooltip_str;
        });
        chart.showLabels(true);
        chart.donut(false);
        chart.showLegend(true);
        chart
            .x(function(d) { return d.label })
            .y(function(d) { return d.value });
        chart.width(450);
        chart.height(450);
            d3.select('#piechart svg')
            .datum(datum)
            .transition().duration(500)
            .attr('width', 450)
            .attr('height', 450)
            .call(chart);
    });

</script>

